CREATE TABLE IF NOT EXISTS `aircraft_event` (
      `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
      `region_id` INT(10) UNSIGNED NOT NULL,
      `data` JSON NOT NULL,
      `startsAt` DATE NOT NULL,
      `endsAt` DATE NOT NULL,
      `created` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
      `updated` DATETIME NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,
      PRIMARY KEY(`id`),
      INDEX(`startsAt`),
      INDEX(`endsAt`),
      INDEX(`created`),
      INDEX(`updated`),
      INDEX(`region_id`),
      CONSTRAINT pk_pevt_regid FOREIGN KEY (`region_id`) REFERENCES `region`(`id`)
) AUTO_INCREMENT=1 ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

