INSERT INTO `app_quote` VALUES (12036,1208,'text','{\"_text\": {\"en\": \"By constantly challenging ourselves, the boundaries of what is possible, we have created solutions that outperform, again and again.\"}}',NULL,'2018-03-29 16:24:45','2018-05-15 17:43:57'),(12037,1208,'text','{\"_text\": {\"en\": \"We are a business driven by our passion and commitment.\"}}',NULL,'2018-03-29 17:06:44','2018-05-15 17:43:57'),(12038,1208,'text','{\"_text\": {\"en\": \"We hold an unshakeable belief that there is always a better solution.\\r\\n\"}}',NULL,'2018-03-29 17:07:02','2018-05-15 17:43:57'),(12039,1208,'text','{\"_text\": {\"en\": \"Its more than a way of working, it’s our whole approach to life that comes from inside, hardwired into our dna.\\r\\n\"}}',NULL,'2018-03-29 17:07:17','2018-05-15 17:43:57'),(12040,1208,'text','{\"_text\": {\"en\": \"At Embraer, we believe that humankind’s greatest achievements have resulted from overcoming their greatest challenges.\"}}',NULL,'2018-03-29 17:07:43','2018-05-15 17:43:57'),(12041,1208,'text','{\"_text\": {\"en\": \"At Embraer we deliberately take on our customers’ world of challenges, apply a fresh perspective and create solutions that enable them to outperform.\"}}',NULL,'2018-03-29 17:08:07','2018-05-15 17:43:57');